import React, { useState, useEffect } from 'react';
import { authFetch } from '../utils/api';
import { useNavigate } from 'react-router-dom';

export default function Home(){
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(()=> {
    async function load() {
      const token = sessionStorage.getItem('sessionToken');
      if (!token) { navigate('/login'); return; }
      try {
        const res = await authFetch('/api/me', 'GET', null, token);
        setUser(res.user);
      } catch (err) {
        console.error('Unauthorized', err);
        sessionStorage.removeItem('sessionToken');
        sessionStorage.removeItem('user');
        navigate('/login');
      }
    }
    load();
  }, [navigate]);

  const handleLogout = async () => {
    const token = sessionStorage.getItem('sessionToken');
    try {
      await fetch((process.env.REACT_APP_API_BASE || 'http://localhost:5000') + '/auth/logout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
        body: JSON.stringify({ token })
      });
    } catch (e) {}
    sessionStorage.removeItem('sessionToken');
    sessionStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3>Home</h3>
        <div>
          {user && <span className="me-3">Hello, {user.name || user.email}</span>}
          <button className="btn btn-outline-danger btn-sm" onClick={handleLogout}>Logout</button>
        </div>
      </div>
      <p>Welcome to the mini course app. This route is protected and required a session token stored in sessionStorage.</p>
    </div>
  );
}
