import React, { useState, useEffect } from "react";
import { authFetch } from "../utils/api";
import { useNavigate } from "react-router-dom";
import "../styles/Home.css"; // ðŸ‘ˆ CSS IMPORT

export default function Home() {
  const [user, setUser] = useState(null);
  const [courses, setCourses] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function load() {
      const token = sessionStorage.getItem("sessionToken");
      if (!token) return navigate("/login");

      try {
        const res = await authFetch("/api/me", "GET", null, token);
        setUser(res.user);

        const courseRes = await authFetch("/api/courses", "GET", null, token);
        setCourses(courseRes.courses);
      } catch (err) {
        sessionStorage.removeItem("sessionToken");
        sessionStorage.removeItem("user");
        navigate("/login");
      }
    }
    load();
  }, [navigate]);

  const handleLogout = async () => {
    const token = sessionStorage.getItem("sessionToken");

    await fetch("http://localhost:5000/auth/logout", {
      method: "POST",
      headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify({ token }),
    });

    sessionStorage.clear();
    navigate("/login");
  };

  return (
    <div className="home-container">
      
      {/* HEADER */}
      <div className="home-header">
        <h2 className="home-title">Welcome, {user?.name || user?.email}</h2>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <p className="home-subtitle">Explore your available courses below</p>

      {/* COURSES GRID */}
      <div className="course-grid">
        {courses.map((course) => (
          <div key={course._id} className="course-card">

            <h3 className="course-title">{course.title}</h3>
            <p className="course-desc">{course.description}</p>

            <p className="course-price">
              {course.price === 0 ? (
                <span className="free-tag">Free</span>
              ) : (
                `â‚¹${course.price}`
              )}
            </p>

            <button className="course-btn">View Course</button>
          </div>
        ))}
      </div>
    </div>
  );
}
